import styles from "./styles.module.css";

import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const API_KEY = "af42c4ed74b14e52882a06c3ac93ec34";
  const url = "https://newsapi.org/v2/everything?q=";

  const [articles, setArticles] = useState([]);
  const [showWelcomeMessage, setShowWelcomeMessage] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [mobileSearchQuery, setMobileSearchQuery] = useState("");
  const [mobileMenuHidden, setMobileMenuHidden] = useState(true);

  useEffect(() => {
    fetchData("all");
  }, []);

  const fetchData = async (query) => {
    try {
      const res = await axios.get(`${url}${query}&apiKey=${API_KEY}`);
      setArticles(res.data.articles);
      setShowWelcomeMessage(false);
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };

  const toggleMobileMenu = () => {
    setMobileMenuHidden(!mobileMenuHidden);
  };

  const search = (query) => {
    fetchData(query || searchQuery);
  };

  const searchMobile = () => {
    fetchData(mobileSearchQuery);
  };

  return (
    <div>
      <header>
        <img src="./logo.png" width="200px" style={{ position: "relative", left: "5%" }} alt="" />
        <div className="inputSearch desktop">
          <form onSubmit={(e) => { e.preventDefault(); search(); }}>
            <input type="text" placeholder="Type to search..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
            <span> <i className="fas fa-search"></i></span>
          </form>
        </div>
        <nav className="desktop">
          <ul>
            <li onClick={() => search("India")}>India</li>
            <li onClick={() => search("cricket")}>Cricket</li>
            <li onClick={() => search("tech")}>Tech</li>
            <li onClick={() => search("Stocks")}>Stocks</li>
            <li onClick={() => search("movies")}>Movies</li>
            <li onClick={() => search("business")}>Business</li>
          </ul>
        </nav>
        <div className="menuBtn" onClick={toggleMobileMenu}>
          <i className="fas fa-bars"></i>
        </div>
        <div className={`mobile ${mobileMenuHidden ? 'hidden' : ''}`}>
          <nav>
            <ul>
              <li onClick={() => search("India")}>India</li>
              <li onClick={() => search("cricket")}>Cricket</li>
              <li onClick={() => search("tech")}>Tech</li>
              <li onClick={() => search("Stocks")}>Stocks</li>
              <li onClick={() => search("movies")}>Movies</li>
              <li onClick={() => search("business")}>Business</li>
            </ul>
          </nav>
          <div className="inputSearch">
            <form onSubmit={(e) => { e.preventDefault(); searchMobile(); }}>
              <input type="text" placeholder="Type to search..." value={mobileSearchQuery} onChange={(e) => setMobileSearchQuery(e.target.value)} />
              <span> <i className="fas fa-search"></i></span>
            </form>
          </div>
        </div>
      </header>
      <main>
        <div className="welcome-message" style={{ position: "relative", top: "290px" }} hidden={!showWelcomeMessage}>
          <h1>Welcome to The Times</h1>
          <p>Explore the latest news from around the world.</p>
        </div>
        {articles.map((article, index) => (
          <div key={index} className="card box">
            <a href={article.url}>
              <img src={article.urlToImage} alt={article.title} />
              <h4>{article.title}</h4>
              <div className="publishbyDate">
                <p>{article.source.name}</p>
                <span>•</span>
                <p>{new Date(article.publishedAt).toDateString()}</p>
              </div>
              <div className="desc">{article.description}</div>
            </a>
          </div>
        ))}
      </main>
    </div>
  );
}

export default App;
