import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

import styles from "./styles.module.css"; // Importing CSS module

function Main() {
  const API_KEY = "af42c4ed74b14e52882a06c3ac93ec34";
  const url = "https://newsapi.org/v2/everything?q=";

  const [articles, setArticles] = useState([]);
  const [showWelcomeMessage, setShowWelcomeMessage] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const history = useHistory(); // Initialize useHistory

  useEffect(() => {
    fetchData("all");
  }, []);

  const fetchData = async (query) => {
    try {
      const res = await axios.get(`${url}${query}&apiKey=${API_KEY}`);
      setArticles(res.data.articles);
      setShowWelcomeMessage(false);
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };

  const search = (query) => {
    fetchData(query || searchQuery);
  };

  const handleLogout = () => {
    // Remove user token from local storage
    localStorage.removeItem("token");
    // Redirect user to the login page
    history.push("/login");
  };

  return (
    <div>
      <header className={styles.header}>
        <img src="./logo.png" width="200px" className={styles.logo} alt="" />
        <div className={`${styles.inputSearch} ${styles.desktop}`}>
          <form onSubmit={(e) => { e.preventDefault(); search(); }}>
            <input type="text" placeholder="Type to search..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
            <span> <i className="fas fa-search"></i></span>
          </form>
        </div>
        <nav className={styles.desktop}>
          <ul>
            <li onClick={() => search("India")}>India</li>
            <li onClick={() => search("cricket")}>Cricket</li>
            <li onClick={() => search("tech")}>Tech</li>
            <li onClick={() => search("Stocks")}>Stocks</li>
            <li onClick={() => search("movies")}>Movies</li>
            <li onClick={() => search("business")}>Business</li>
          </ul>
        </nav>
        <button onClick={handleLogout} className={styles.logoutButton}>Logout</button> {/* Add a logout button */}
      </header>
      <main>
        <div className={styles.welcomeMessage} hidden={!showWelcomeMessage}>
          <h1>Welcome to The Times</h1>
          <p>Explore the latest news from around the world.</p>
        </div>
        {articles.map((article, index) => (
          <div key={index} className={`${styles.card} ${styles.box}`}>
            <a href={article.url}>
              <img src={article.urlToImage} alt={article.title} />
              <h4>{article.title}</h4>
              <div className={styles.publishByDate}>
                <p>{article.source.name}</p>
                <span>•</span>
                <p>{new Date(article.publishedAt).toDateString()}</p>
              </div>
              <div className={styles.desc}>{article.description}</div>
            </a>
          </div>
        ))}
      </main>
    </div>
  );
}

export default Main;
