import React, { createContext, useState, useContext } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [userData, setUserData] = useState({ email: "Roshan", password: "" }); // Make sure userData is initialized properly

  return (
    <AuthContext.Provider value={{ userData, setUserData }}> {/* Provide userData as the context value */}
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  return useContext(AuthContext);
};
