import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Assuming you use axios for API requests

function MainController() {
  const [searchQuery, setSearchQuery] = useState('');
  const [mobileSearchQuery, setMobileSearchQuery] = useState('');
  const [mobileMenuHidden, setMobileMenuHidden] = useState(true);
  const [articles, setArticles] = useState([]);
  const [showWelcomeMessage, setShowWelcomeMessage] = useState(true);

  useEffect(() => {
    // Fetch articles when component mounts
    fetchArticles();
  }, []);

  const fetchArticles = async () => {
    try {
      // Fetch articles from your API
      const response = await axios.get('your-api-url');
      setArticles(response.data.articles);
    } catch (error) {
      console.error('Error fetching articles:', error);
    }
  };

  const search = (query) => {
    // Your search logic here
  };

  const searchMobile = (query) => {
    // Your mobile search logic here
  };

  const toggleMobileMenu = () => {
    setMobileMenuHidden(!mobileMenuHidden);
  };

  return (
    <>
      <header>
        <img src="./logo.png" width="200px" style={{ position: 'relative', left: '5%' }} alt="" />

        <div className="inputSearch desktop">
          <form onSubmit={search}>
            <input type="text" placeholder="Type to search..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
            <span> <i className="fa-solid fa-search"></i></span>
          </form>
        </div>
        <nav className="desktop">
          <ul>
            <li onClick={() => search('India')}>India</li>
            <li onClick={() => search('cricket')}>Cricket</li>
            <li onClick={() => search('tech')}>Tech</li>
            <li onClick={() => search('Stocks')}>Stocks</li>
            <li onClick={() => search('movies')}>Movies</li>
            <li onClick={() => search('business')}>Business</li>
          </ul>
        </nav>

        <div className="menuBtn" onClick={toggleMobileMenu}>
          <i className="fa-solid fa-bars"></i>
        </div>

        <div className={`mobile ${mobileMenuHidden ? 'hidden' : ''}`}>
          <nav>
            <ul>
              <li onClick={() => search('India')}>India</li>
              <li onClick={() => search('cricket')}>Cricket</li>
              <li onClick={() => search('tech')}>Tech</li>
              <li onClick={() => search('Stocks')}>Stocks</li>
              <li onClick={() => search('movies')}>Movies</li>
              <li onClick={() => search('business')}>Business</li>
            </ul>
          </nav>
          <div className="inputSearch">
            <form onSubmit={searchMobile}>
              <input type="text" placeholder="Type to search..." value={mobileSearchQuery} onChange={(e) => setMobileSearchQuery(e.target.value)} />
              <span> <i className="fa-solid fa-search"></i></span>
            </form>
          </div>
        </div>
      </header>

      {/* main  */}

      <main>
        <div className="welcome-message" style={{ position: 'relative', top: '290px' }} >
          {showWelcomeMessage &&
            <>
              <h1>Welcome to The Times</h1>
              <p>Explore the latest news from around the world.</p>
            </>
          }
        </div>
        {articles.map(article => (
          <div key={article.id} className="card box">
            <a href={article.url}>
              <img src={article.urlToImage} alt="Article" />
              <h4>{article.title}</h4>
              <div className="publishbyDate">
                <p>{article.source.name}</p>
                <span>â€¢</span>
                <p>{article.publishedAt}</p>
              </div>
              <div className="desc">
                {article.description}
              </div>
            </a>
          </div>
        ))}
      </main>
    </>
  );
}

export default MainController;
