import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import customStyles from "./customStyles.module.css";

const SignUp = () => {
	const [formData, setFormData] = useState({
		firstName: "",
		lastName: "",
		email: "",
		password: "",
	});
	const [error, setError] = useState("");
	const navigate = useNavigate();

	const handleChange = ({ currentTarget: input }) => {
		setFormData({ ...formData, [input.name]: input.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const url = "http://localhost:8080/api/users";
			const { data: res } = await axios.post(url, formData);
			navigate("/login");
			console.log(res.message);
		} catch (error) {
			if (
				error.response &&
				error.response.status >= 400 &&
				error.response.status <= 500
			) {
				setError(error.response.data.message);
			}
		}
	};

	return (
		<div className={customStyles.signup_container}>
			<div className={customStyles.signup_form_container}>
				<div className={customStyles.left}>
					<h1>Welcome Back</h1>
					<Link to="/login">
						<button type="button" className={customStyles.white_btn}>
							Sign in
						</button>
					</Link>
				</div>
				<div className={customStyles.right}>
					<form className={customStyles.form_container} onSubmit={handleSubmit}>
						<h1>Create Account</h1>
						<input
							type="text"
							placeholder="First Name"
							name="firstName"
							onChange={handleChange}
							value={formData.firstName}
							required
							className={customStyles.input}
						/>
						<input
							type="text"
							placeholder="Last Name"
							name="lastName"
							onChange={handleChange}
							value={formData.lastName}
							required
							className={customStyles.input}
						/>
						<input
							type="email"
							placeholder="Email"
							name="email"
							onChange={handleChange}
							value={formData.email}
							required
							className={customStyles.input}
						/>
						<input
							type="password"
							placeholder="Password"
							name="password"
							onChange={handleChange}
							value={formData.password}
							required
							className={customStyles.input}
						/>
						{error && <div className={customStyles.error_msg}>{error}</div>}
						<button type="submit" className={customStyles.green_btn}>
							Sign Up
						</button>
					</form>
				</div>
			</div>
		</div>
	);
};

export default SignUp;
