import styles from "./styles.module.css";
import react, { useState, useEffect } from 'react';

// import { getNews, deleteUser } from '../Service/api';
import { getNews } from '../Service/api';
import { Link } from 'react-router-dom';


const AllNews = () => {
    const [news, setNews] = useState([]);

    useEffect(() => {
        getAllNews();
    }, []);

    const handleLogout = () => {
      localStorage.removeItem("token");
      window.location.reload();
    };

    // const deleteUserData = async (id) => {
    //     await deleteUser(id);
    //     getAllNews();
    // }

    const getAllNews = async () => {
        let response = await getNews();
        console.log(response.data);
        setNews(response.data);
    }


    return (
         <>
        <div>
      <header className={styles.header}>
        <img src="/logo.png" width="200px" className={styles.logo} alt="Your company logo" />
        <nav className={styles.desktop}>
          <ul>

          </ul>
          <button className={styles.white_btn} onClick={handleLogout}>
            Logout ▶️
          </button>
        </nav>
      </header>
      <main>
        {news.map((n, index) => (
            <div key={index} className={`${styles.card} ${styles.box}`}>
              <a href={n.url}>
                <div>
                {n.iurl && <img src={n.iurl} alt={n.title} />}
                </div>
                <h4>{n.title}</h4>

                <div className={styles.publishByDate}>
                  <hr />
                  <p>{n.web}</p>
                  <hr />
                </div>
                <div className={styles.desc}>{n.desc}</div>
              </a>
            </div>
          ))}
      </main>
    </div>
        </>
    )
}

export default AllNews;
